import React from 'react'
import { Button, Form, Icon, Input, Cascader } from 'antd';
import { connect } from 'react-redux'
import { sendStudentData } from './../../redux/action'
import majorOptions from '../../list'
const FormItem = Form.Item;

class Login extends React.Component {



  /*
  如何避免输入为空？？？？？
  preProcessing = () =>{
    this.setState({
      "studentID": '0' ,
      "studentPassword": '0' ,
    });
  };
  */

  //传递学生数据
  handleClick = () =>{
    const { dispatch } = this.props;
    dispatch(sendStudentData(this.state.studentID, this.state.studentPassword));
    console.log(this.state.studentID, this.state.studentPassword);
  };

  //读取学生个人数据
  handelChange = (studentData) =>{
    switch (studentData.target.id) {
      case "sd":
        this.setState({
          "studentID": studentData.target.value ,
        });
        break;
      case "pw":
        this.setState({
          "studentPassword": studentData.target.value ,
        });
        break;
      default:
        break;
    }
  };

  render() {

    return(
        <div>
            <Form className="login">
              <FormItem>
                <Input prefix={<Icon type="user"/>} id="sd" placeholder="学号" onChange={this.handelChange.bind(this)}/>
              </FormItem>
              <FormItem>
                <Input prefix={<Icon type="lock"/>} id="pw" type="password" placeholder="密码" onChange={this.handelChange.bind(this)}/>
              </FormItem>
              <FormItem>
                <Cascader options={majorOptions}/>
              </FormItem>
              <FormItem>
                <Button htmlType="submit" className="login-form-button"  onClick={this.handleClick.bind(this)}>查询</Button>
              </FormItem>
            </Form>
        </div>
    )
  }
}

export default connect()(Login)
