import axios from 'axios'

/*
* 发送请求获取成绩数据*/
export default function request(studentID, studentPassword, thisP){

  return new Promise((resolve, reject)=>{
    axios({
      method: 'post',
      datatype: 'json',
      url: 'http://localhost:1323/test',
      data: {
        Sid: studentID,
        Pwd: studentPassword,
      }
    }).then(function (response) {
      console.log(response);
      if (response.status === 200){
        console.log(response.data);
        let result = response.data;
        console.log(result);
        resolve(thisP.setState({
          "J": result.isAttendJLesson,
          "S": result.isAttendSLesson,
          "FL": result.isAttendFLesson,
          "GC": result.totalCreditOfGeneralCourse,
          "PA": result.totalCreditOfPACourse,
          "NS": result.totalCreditOfNSCourse,
          "NC": result.totalCreditOfNCourse,
          "CC": result.totalCreditOfCCourse,
          "Credit": result.totalCreditOfCourse,
        }));
        } else {
        reject(response.data);
      }
    }).catch(function (error) {
      console.log(error);
    });
  })
};
