import React from 'react'
import { Button, Form, Icon, Input, Cascader } from 'antd';
import { connect } from 'react-redux'
import { sendStudentData } from './../../redux/action'
import majorOptions from '../../list'
const FormItem = Form.Item;




class Login extends React.Component {

  //初始化
  constructor(props){
    super(props);

    this.state = {
      "studentID": '' ,
      "studentPassword": '' ,
      "studentMajor": '' ,
    };
  }

  //传递学生数据
  handleClick = () =>{
    const { dispatch } = this.props;
    dispatch(sendStudentData(this.state.studentID, this.state.studentPassword, this.state.studentMajor));
    console.log(this.state.studentID, this.state.studentPassword, this.state.studentMajor);
  };

  //读取学生个人数据
  handleChange = (studentData) =>{
    switch (studentData.target.id) {
      case "sd":
        this.setState({
          "studentID": studentData.target.value ,
        });
        break;
      case "pw":
        this.setState({
          "studentPassword": studentData.target.value ,
        });
        break;
      default:
        break;
    }
  };

  handleChoose = (studentData) =>{
    this.setState({
      "studentMajor": studentData.value ,
    });
  };

  render() {

    return(
        <div>
            <Form className="login">
              <FormItem>
                <Input prefix={<Icon type="user"/>} id="sd" placeholder="学号" onChange={this.handleChange.bind(this)}/>
              </FormItem>
              <FormItem>
                <Input prefix={<Icon type="lock"/>} id="pw" type="password" placeholder="密码" onChange={this.handleChange.bind(this)}/>
              </FormItem>
              <FormItem>
                <Cascader  options={majorOptions} id="mo" placeholder="选择专业" onChange={this.handleChoose.bind(this)}/>
              </FormItem>
              <FormItem>
                <Button htmlType="submit" className="login-form-button"  onClick={this.handleClick.bind(this)}>查询</Button>
              </FormItem>
            </Form>
        </div>
    )
  }
}

export default connect()(Login)
